using Newtonsoft.Json;
using System;
using System.Text;
using UnityEngine.Playables;

namespace Client
{
    public struct BridgeEventMessage
    {
        public static BridgeEventMessage DeserializeJson(string json) => JsonConvert.DeserializeObject<BridgeEventMessage>(json, new JsonSerializerSettings
        {
            NullValueHandling = NullValueHandling.Ignore
        });
        public string data { get; set; }
        public string eventKey { get; set; }
    }
    public struct CharacterMessage
    {
        public static CharacterMessage DeserializeJson(string json) => JsonConvert.DeserializeObject<CharacterMessage>(json, new JsonSerializerSettings
        {
            NullValueHandling = NullValueHandling.Ignore
        });
        public MessageAnimationInfo messageAnimationInfo { get; set; }
        public int characterId { get; set; }
        public string audioBytes64 { get; set; }
        public PlayerState playerState { get; set; }

        public void Copy(CharacterMessage from)
        {
            messageAnimationInfo = from.messageAnimationInfo;
            characterId = from.characterId;
            audioBytes64 = from.audioBytes64;
            playerState = from.playerState;
        }
    }
    public struct MessageCameraInfo
    {
        public int characterId;
    }

    public enum PlayerState { 
        play, pause, stop
    }
}

